SELECT A.shop_name as shopName , OL.order_name as orderName , B.master_code as masterCode , B.shop_idx as shopIdx , B.combine_code as combineCode , B.seller_name as sellerName , B.seller_phone as sellerPhone , B.seller_country_code as sellerCountryCode , B.seller_country as sellerCountry , B.seller_city as sellerCity , B.seller_zip_code as sellerZipCode , B.seller_addr1 as sellerAddr1 , B.seller_addr2 as sellerAddr2 , IFNULL(B.buyer_firstname,'') as buyerFirstname , IFNULL(B.buyer_lastname,'') as buyerLastname , IFNULL(B.buyer_phone,'') as buyerPhone , B.buyer_email as buyerEmail , B.buyer_country_code as buyerCountryCode , B.buyer_country as buyerCountry , B.buyer_city as buyerCity , B.buyer_province as buyerProvince , B.buyer_zip_code as buyerZipCode , B.buyer_addr1 as buyerAddr1 , B.buyer_addr2 as buyerAddr2 , B.box_length as boxLength , B.box_width as boxWidth , B.box_height as boxHeight , B.box_unit as boxUnit , B.box_weight as boxWeight , B.weight_unit as weightUnit , B.state , DATE_FORMAT(B.reg_date,'%Y-%m-%d') as regDate , P.invoice , FN_LOCALE_COURIER(P.courier, #{locale}) as courier , (select max(goods) from tb_order_detail where order_idx = OL.order_idx) as goods , (select count(goods_item_id)-1 from tb_delivery_sku where master_code = B.master_code) as goodsCnt , OL.order_code as orderCode , DATE_FORMAT(IFNULL(B.up_date,now()),'%Y-%m-%d') as orderDate , pay_state as payState , FN_LOCALE_MESSAGE('A010000', B.state, #{locale}) as stateStr , (case when B.state = 'A010010' then ' paym' when B.state = 'A010020' or B.state = 'A010025' then ' deliver' when B.state = 'A010025' then ' deliver' when B.state = 'A010030' then ' deliver2' when B.state = 'A010040' then ' deliver3' when B.state = 'A010050' then ' deliver4' when B.state = 'A010060' then ' deliver5' else ' paym' end ) as stateStrCss ,(P.payment * FN_DISCOUNT_COURIER(#{email},P.courier_company,P.courier) * 0.01) as rankPrice ,P.payment ,P.rank_price ,IFNULL((select max(local_code) from tb_delivery_local_d