ORDER BY orderName ORDER BY orderName DESC ORDER BY buyerCountryCode ORDER BY buyerCountryCode DESC ORDER BY goods ORDER BY goods DESC ORDER BY companyName ORDER BY companyName DESC ORDER BY orderDate ORDER BY orderDate DESC ORDER BY stateStr ORDER BY stateStr DESC ORDER BY B.state, orderDate DESC
LIMIT #{startRow}, #{pageSize}

