mapper.select:
SELECT API.confirmation_url as apiConfirmationUrl ,API.id as apiId ,API.name as apiName ,API.client_id as apiClientId ,API.price as apiPrice ,A.shop_name as shopName ,B.master_code as masterCode ,O.order_code as orderCode FROM tb_shop as A join tb_delivery as B on A.shop_idx = B.shop_idx join tb_delivery_order O on B.master_code = O.master_code join tb_order_list OL on O.order_code = OL.order_code and A.shop_idx=OL.shop_idx join tb_pay_info PI on B.master_code = PI.master_code join tb_shopify_api_applicationchg API on PI.pay_id = API.pay_id WHERE O.hide_yn = 'N' AND OL.hide_yn = 'Y' AND OL.order_idx in (select order_idx from tb_order_detail where order_idx = OL.order_idx and del_yn = 'N') AND A.email = #{email} AND PI.total_amount > 0
AND B.master_code = #{masterCode}
AND API.id = #{apiId}

